import React, { useState, useEffect, useContext } from 'react';
import { Link, useLocation, useParams } from 'react-router-dom';
import { Container, Row, Col, Button, Form, Card } from 'react-bootstrap';
import QuestionCard from '../components/QuestionCard';
import { questionService, tagService } from '../services/api';
import { AuthContext } from '../services/AuthContext';

const HomePage = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('newest');
  const { isAuthenticated } = useContext(AuthContext);

  const location = useLocation();
  const { tagName } = useParams();
  const searchQuery = new URLSearchParams(location.search).get('search');

  useEffect(() => {
    const fetchQuestions = async () => {
      setLoading(true);
      try {
        console.log("Fetching questions with params:", { searchQuery, tagName });
        let response;

        if (searchQuery) {
          response = await questionService.searchQuestions(searchQuery);
        } else if (tagName) {
          // Tag URL changed from /tags/:tagName to /tag/name/:tagName
          response = await questionService.getQuestionsByTag(tagName);
        } else {
          response = await questionService.getAllQuestions();
        }

        console.log("Questions response:", response);

        // Ensure we have an array of questions
        const questionsData = response.data || [];
        console.log("Questions data:", questionsData);

        // Sort questions based on filter
        let sortedData = [...questionsData];
        if (filter === 'newest') {
          sortedData.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        } else if (filter === 'votes') {
          sortedData.sort((a, b) => b.votes - a.votes);
        } else if (filter === 'answers') {
          sortedData.sort((a, b) => (b.answersCount || 0) - (a.answersCount || 0));
        }

        setQuestions(sortedData);
        setError(null);
      } catch (err) {
        console.error('Error details:', err.response || err);
        setError('Failed to load questions. Please try again later.');
        console.error('Error fetching questions:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [searchQuery, tagName, filter]);

  // Display a hero section for new/non-authenticated users
  const renderHero = () => {
    if (searchQuery || tagName || isAuthenticated) return null;

    return (
      <div className="mb-5 position-relative overflow-hidden">
        <div className="hero-bg position-absolute top-0 start-0 end-0 bottom-0" 
             style={{
               background: "radial-gradient(circle at top right, rgba(139, 92, 246, 0.15), transparent 70%), radial-gradient(circle at bottom left, rgba(79, 70, 229, 0.1), transparent 50%)",
               zIndex: -1
             }}>
          <div className="position-absolute top-0 end-0 mt-5 me-5">
            <div className="blob1"></div>
          </div>
          <div className="position-absolute bottom-0 start-0 mb-5 ms-5">
            <div className="blob2"></div>
          </div>
        </div>

        <div className="glass-card p-5 border-0" style={{borderRadius: "var(--border-radius-2xl)"}}>
          <Row className="align-items-center py-4">
            <Col lg={6} className="mb-5 mb-lg-0">
              <h1 className="display-4 fw-bold mb-4" style={{
                background: "linear-gradient(135deg, #4f46e5 0%, #8b5cf6 50%, #ec4899 100%)",
                WebkitBackgroundClip: "text",
                backgroundClip: "text",
                color: "transparent",
                lineHeight: 1.2
              }}>
                <span className="d-block fade-in" style={{animationDelay: "0.1s"}}>Unlock the Power</span>
                <span className="d-block fade-in" style={{animationDelay: "0.3s"}}>of Community</span>
                <span className="d-block fade-in" style={{animationDelay: "0.5s"}}>Knowledge</span>
              </h1>

              <p className="lead mb-4 fade-in" style={{animationDelay: "0.7s", maxWidth: "500px"}}>
                Join thousands of developers asking questions, finding answers, and sharing knowledge on StackUnderflow.
              </p>

              <div className="d-flex gap-3 flex-wrap fade-in" style={{animationDelay: "0.9s"}}>
                <Button 
                  as={Link} 
                  to="/register" 
                  variant="primary" 
                  size="lg" 
                  className="px-4 rounded-pill fw-bold"
                >
                  <span>Join Community</span>
                </Button>
                <Button 
                  as={Link} 
                  to="/ask" 
                  variant="outline-primary" 
                  size="lg" 
                  className="px-4 rounded-pill"
                >
                  <span>Ask a Question</span>
                </Button>
              </div>

              <div className="mt-4 d-flex gap-3 fade-in" style={{animationDelay: "1.1s"}}>
                <div className="d-flex align-items-center">
                  <div className="me-2 text-primary">
                    <i className="bi bi-people-fill fs-4"></i>
                  </div>
                  <div>
                    <div className="fw-bold">25k+</div>
                    <div className="small text-muted">Community Members</div>
                  </div>
                </div>
                <div className="d-flex align-items-center">
                  <div className="me-2 text-primary">
                    <i className="bi bi-question-circle-fill fs-4"></i>
                  </div>
                  <div>
                    <div className="fw-bold">100k+</div>
                    <div className="small text-muted">Questions Answered</div>
                  </div>
                </div>
              </div>
            </Col>

            <Col lg={6} className="position-relative">
              <div className="hero-animation text-center">
                <svg xmlns="http://www.w3.org/2000/svg" width="320" height="320" fill="currentColor" className="bi bi-stack text-primary opacity-75" viewBox="0 0 16 16">
                  <path d="m14.12 10.163 1.715.858c.22.11.22.424 0 .534L8.267 15.34a.598.598 0 0 1-.534 0L.165 11.555a.299.299 0 0 1 0-.534l1.716-.858 5.317 2.659c.505.252 1.1.252 1.604 0l5.317-2.66zM7.733.063a.598.598 0 0 1 .534 0l7.568 3.784a.3.3 0 0 1 0 .535L8.267 8.165a.598.598 0 0 1-.534 0L.165 4.382a.299.299 0 0 1 0-.535L7.733.063z"/>
                  <path d="m14.12 6.576 1.715.858c.22.11.22.424 0 .534l-7.568 3.784a.598.598 0 0 1-.534 0L.165 7.968a.299.299 0 0 1 0-.534l1.716-.858 5.317 2.659c.505.252 1.1.252 1.604 0l5.317-2.659z"/>
                </svg>

                <div className="position-absolute" style={{top: '15%', right: '10%', zIndex: 1}}>
                  <div className="glass-card p-3 shadow-lg slide-in" style={{animationDelay: "0.6s", maxWidth: "200px", borderRadius: "var(--border-radius-lg)"}}>
                    <div className="d-flex align-items-center">
                      <div className="user-avatar-sm me-2">D</div>
                      <div className="small fw-bold">DeveloperPro</div>
                    </div>
                    <div className="mt-2 small text-start">How do I implement JWT in Spring Boot?</div>
                  </div>
                </div>

                <div className="position-absolute" style={{bottom: '20%', left: '5%', zIndex: 1}}>
                  <div className="glass-card p-3 shadow-lg slide-in" style={{animationDelay: "1.0s", maxWidth: "200px", borderRadius: "var(--border-radius-lg)"}}>
                    <div className="d-flex align-items-center">
                      <div className="user-avatar-sm me-2">S</div>
                      <div className="small fw-bold">SpringExpert</div>
                    </div>
                    <div className="mt-2 small text-start">âœ… I've solved this issue before, here's how...</div>
                  </div>
                </div>

                <div className="position-absolute" style={{top: '60%', right: '20%', zIndex: 1}}>
                  <div className="glass-card p-2 shadow-lg slide-in" style={{animationDelay: "1.4s", maxWidth: "150px", borderRadius: "var(--border-radius-lg)"}}>
                    <div className="small text-center">
                      <i className="bi bi-star-fill text-warning me-1"></i>
                      <span className="fw-bold">Solution Found!</span>
                    </div>
                  </div>
                </div>
              </div>
            </Col>
          </Row>
        </div>
      </div>
    );
  };

  // Floating action button for quick ask
  const renderFloatingAskButton = () => {
    if (!isAuthenticated || searchQuery || tagName) return null;

    return (
      <Button 
        as={Link} 
        to="/ask" 
        variant="primary" 
        className="btn-floating pulse"
      >
        <i className="bi bi-plus-lg"></i>
      </Button>
    );
  };

  return (
    <Container>
      {renderHero()}

      <Card className="border-0 shadow-lg mb-4" style={{borderRadius: "var(--border-radius-xl)"}}>
        <Card.Header className="bg-white py-3 d-flex justify-content-between align-items-center flex-wrap">
          <h1 className="h4 mb-0">
            {searchQuery 
              ? <><i className="bi bi-search me-2"></i>Search Results for "{searchQuery}"</> 
              : tagName 
                ? <><i className="bi bi-tag me-2"></i>Questions tagged [{tagName}]</> 
                : <><i className="bi bi-question-circle me-2"></i>All Questions</>}
          </h1>
          <Button as={Link} to="/ask" variant="primary" className="rounded-pill px-4">
            <span><i className="bi bi-plus-circle me-2"></i>Ask Question</span>
          </Button>
        </Card.Header>

        <Card.Body className="p-0">
          <div className="p-3 bg-light border-bottom d-flex justify-content-between align-items-center flex-wrap">
            <div>
              <span className="fw-bold">{questions.length}</span> questions
            </div>
            <Form.Select 
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              style={{ width: 'auto' }}
              className="border-0 bg-light"
            >
              <option value="newest">Newest</option>
              <option value="votes">Most Votes</option>
              <option value="answers">Most Answers</option>
            </Form.Select>
          </div>

          {loading ? (
            <div className="text-center py-5">
              <div className="d-flex flex-column align-items-center justify-content-center gap-3">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
                <p className="text-muted">Loading questions...</p>

                {/* Loading skeleton */}
                <div style={{width: "80%", maxWidth: "700px"}}>
                  {[1, 2, 3].map(i => (
                    <div key={i} className="card p-3 mb-3 border-0 bg-transparent">
                      <div className="d-flex">
                        <div style={{width: "70px"}} className="me-3">
                          <div className="skeleton" style={{height: "40px", width: "40px", borderRadius: "8px"}}></div>
                        </div>
                        <div className="flex-grow-1">
                          <div className="skeleton" style={{height: "24px", width: "80%"}}></div>
                          <div className="skeleton" style={{height: "16px", width: "90%"}}></div>
                          <div className="skeleton" style={{height: "16px", width: "60%"}}></div>
                          <div className="d-flex justify-content-between mt-3">
                            <div className="d-flex gap-1" style={{width: "30%"}}>
                              <div className="skeleton" style={{height: "20px", width: "60px", borderRadius: "50px"}}></div>
                              <div className="skeleton" style={{height: "20px", width: "60px", borderRadius: "50px"}}></div>
                            </div>
                            <div className="skeleton" style={{height: "20px", width: "100px"}}></div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ) : error ? (
            <div className="alert alert-danger m-3">{error}</div>
          ) : questions.length === 0 ? (
            <div className="text-center py-5">
              <i className="bi bi-search display-1 d-block mb-3 text-muted opacity-25"></i>
              <p className="text-muted mb-4">No questions found.</p>
              <Button as={Link} to="/ask" variant="primary" className="rounded-pill px-4">
                <span><i className="bi bi-plus-circle me-2"></i>Ask a Question</span>
              </Button>
            </div>
          ) : (
            <div className="question-list">
              {questions.map((question, index) => (
                <div key={question.id} className="fade-in" style={{
                  animationDuration: "0.5s", 
                  animationDelay: `${index * 0.1}s`,
                  animationFillMode: "both"
                }}>
                  <QuestionCard question={question} />
                </div>
              ))}
            </div>
          )}
        </Card.Body>
      </Card>

      {renderFloatingAskButton()}
    </Container>
  );
};

export default HomePage;
