import React, { useState, useEffect } from 'react';
import { Link, useLocation, useParams } from 'react-router-dom';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import QuestionCard from '../components/QuestionCard';
import { questionService } from '../services/api';

const HomePage = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('newest');

  const location = useLocation();
  const { tagName } = useParams();
  const searchQuery = new URLSearchParams(location.search).get('search');

  useEffect(() => {
    const fetchQuestions = async () => {
      setLoading(true);
      try {
        let data;
        if (searchQuery) {
          data = await questionService.searchQuestions(searchQuery);
        } else if (tagName) {
          data = await questionService.getQuestionsByTag(tagName);
        } else {
          data = await questionService.getAllQuestions();
        }

        // Sort questions based on filter
        let sortedData = [...data.data];
        if (filter === 'newest') {
          sortedData.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        } else if (filter === 'votes') {
          sortedData.sort((a, b) => b.votes - a.votes);
        } else if (filter === 'answers') {
          sortedData.sort((a, b) => b.answers - a.answers);
        }

        setQuestions(sortedData);
        setError(null);
      } catch (err) {
        setError('Failed to load questions. Please try again later.');
        console.error('Error fetching questions:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [searchQuery, tagName, filter]);

  return (
    <Container>
      <Row className="mb-4">
        <Col md={8}>
          <h1>
            {searchQuery 
              ? `Search Results for "${searchQuery}"` 
              : tagName 
                ? `Questions tagged [${tagName}]` 
                : 'All Questions'}
          </h1>
        </Col>
        <Col md={4} className="text-end">
          <Button as={Link} to="/ask" variant="primary">Ask Question</Button>
        </Col>
      </Row>

      <Row className="mb-3">
        <Col>
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <strong>{questions.length}</strong> questions
            </div>
            <Form.Select 
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
              style={{ width: 'auto' }}
            >
              <option value="newest">Newest</option>
              <option value="votes">Most Votes</option>
              <option value="answers">Most Answers</option>
            </Form.Select>
          </div>
        </Col>
      </Row>

      {loading ? (
        <div className="text-center py-5">Loading...</div>
      ) : error ? (
        <div className="alert alert-danger">{error}</div>
      ) : questions.length === 0 ? (
        <div className="text-center py-5">
          <p>No questions found.</p>
          <Button as={Link} to="/ask" variant="primary">Ask a Question</Button>
        </div>
      ) : (
        <div>
          {questions.map(question => (
            <QuestionCard key={question.id} question={question} />
          ))}
        </div>
      )}
    </Container>
  );
};

export default HomePage;
