import React, { useContext } from 'react';
import { Card, Button, Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { AuthContext } from '../services/AuthContext';
import { voteService } from '../services/api';

const AnswerItem = ({ answer, isAccepted, onAcceptAnswer, questionOwnerId, currentUserId }) => {
  const { currentUser, isAuthenticated } = useContext(AuthContext);

  const handleVote = async (voteType) => {
    if (!isAuthenticated) return;

    try {
      await voteService.voteAnswer(currentUser.id, answer.id, voteType);
      // Force a refresh to show updated vote count
      window.location.reload();
    } catch (error) {
      // Handle error silently
    }
  };

  // User can accept an answer if:
  // 1. They are authenticated
  // 2. They are the question owner
  const canAccept = isAuthenticated && currentUser?.id === questionOwnerId;

  // Defensive check - if answer is undefined, return nothing
  if (!answer) {
    return null;
  }

  // Calculate vote total
  const voteTotal = (answer.upvotes || 0) - (answer.downvotes || 0);

  // Format date for better display
  const formatDate = (dateString) => {
    if (!dateString) return "unknown date";

    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      // Check if hours or minutes
      const diffHours = Math.floor(diffTime / (1000 * 60 * 60));
      if (diffHours === 0) {
        const diffMinutes = Math.floor(diffTime / (1000 * 60));
        return `${diffMinutes} ${diffMinutes === 1 ? 'minute' : 'minutes'} ago`;
      }
      return `${diffHours} ${diffHours === 1 ? 'hour' : 'hours'} ago`;
    } else if (diffDays === 1) {
      return 'yesterday';
    } else if (diffDays < 7) {
      return `${diffDays} days ago`;
    } else {
      return date.toLocaleDateString('en-US', { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric' 
      });
    }
  };

  // Get first letter of username for avatar
  const getInitial = (username) => {
    return username && username.length > 0 ? username.charAt(0).toUpperCase() : 'U';
  };

  return (
    <div className={`answer-container p-4 mb-4 bg-white rounded-lg shadow-sm ${isAccepted ? 'accepted-answer' : ''}`}>
      <div className="position-relative">
        {isAccepted && (
          <Badge 
            bg="success" 
            className="position-absolute top-0 end-0 px-3 py-2 d-flex align-items-center"
          >
            <i className="bi bi-check-circle-fill me-1"></i>
            Accepted Answer
          </Badge>
        )}

        <div className="d-flex">
          <div className="vote-buttons">
            <Button 
              variant="outline-secondary" 
              size="sm"
              onClick={() => handleVote('UP')}
              disabled={!isAuthenticated}
              aria-label="Upvote"
              className="rounded-circle"
            >
              <i className="bi bi-caret-up-fill"></i>
            </Button>
            <div className="vote-count">{voteTotal}</div>
            <Button 
              variant="outline-secondary" 
              size="sm"
              onClick={() => handleVote('DOWN')}
              disabled={!isAuthenticated}
              aria-label="Downvote"
              className="rounded-circle"
            >
              <i className="bi bi-caret-down-fill"></i>
            </Button>
          </div>

          <div className="flex-grow-1">
            <div className="answer-text mb-3">
              <div dangerouslySetInnerHTML={{ __html: answer.text.replace(/\n/g, '<br>') }}></div>
            </div>

            {/* If image field is actually used for code */}
            {answer.image && (
              <div className="code-block mt-3 mb-4 bg-light p-3 rounded">
                <pre className="mb-0">
                  {answer.image}
                </pre>
              </div>
            )}

            <div className="d-flex justify-content-between align-items-center flex-wrap mt-4">
              {canAccept && !isAccepted && (
                <Button 
                  variant="outline-success" 
                  size="sm"
                  onClick={() => onAcceptAnswer(answer.id)}
                  className="rounded-pill px-3"
                >
                  <i className="bi bi-check-circle me-2"></i>
                  Accept Answer
                </Button>
              )}

              <div className="question-meta d-flex align-items-center ms-auto small text-muted">
                <span className="me-3">
                  <i className="bi bi-clock me-1"></i>
                  {formatDate(answer.createdAt)}
                </span>
                <Link to={`/users/${answer.authorId}`} className="text-decoration-none d-flex align-items-center">
                  <div className="user-avatar-sm me-2">
                    {getInitial(answer.authorUsername)}
                  </div>
                  <span className="text-secondary">{answer.authorUsername || 'User'}</span>
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnswerItem;
