import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Card, Nav, Button, Alert } from 'react-bootstrap';
import { userService, questionService } from '../services/api';
import { AuthContext } from '../services/AuthContext';
import QuestionCard from '../components/QuestionCard';

const UserProfilePage = () => {
  const { id } = useParams();
  const { currentUser, isModerator } = useContext(AuthContext);

  const [user, setUser] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('profile');

  useEffect(() => {
    console.log("UserProfilePage mounted with ID:", id);
    console.log("Current authenticated user:", currentUser);

    const fetchUserData = async () => {
      setLoading(true);
      try {
        console.log("Fetching user data for ID:", id);
        const userResponse = await userService.getUserById(id);
        console.log("User data response:", userResponse);
        setUser(userResponse.data);

        if (activeTab === 'questions') {
          console.log("Fetching questions for user ID:", id);
          const questionsResponse = await questionService.getQuestionsByAuthor(id);
          console.log("User questions response:", questionsResponse);
          setQuestions(questionsResponse.data);
        }

        setError(null);
      } catch (err) {
        console.error('Error details:', err.response || err);
        setError('Failed to load user profile. Please try again later.');
        console.error('Error fetching user data:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [id, activeTab, currentUser]);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const isOwnProfile = currentUser && currentUser.id === parseInt(id);

  if (loading) {
    return <Container className="py-5 text-center">Loading...</Container>;
  }

  if (error || !user) {
    return (
      <Container className="py-5">
        <Alert variant="danger">{error || 'User not found'}</Alert>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <Row>
        <Col md={3}>
          <Card>
            <Card.Body className="text-center">
              <div className="mb-3">
                <img
                  src={`https://ui-avatars.com/api/?name=${user.username}&background=random&size=128`}
                  alt={user.username}
                  className="rounded-circle img-thumbnail"
                />
              </div>
              <h4>{user.username}</h4>
              <p className="text-muted">
                Member since {new Date(user.createdAt).toLocaleDateString()}
              </p>
              {user.isModerator && (
                <div className="mb-2">
                  <span className="badge bg-primary">Moderator</span>
                </div>
              )}
              {user.banned && (
                <div className="mb-2">
                  <span className="badge bg-danger">Banned</span>
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>

        <Col md={9}>
          <Card>
            <Card.Header>
              <Nav variant="tabs" defaultActiveKey="profile">
                <Nav.Item>
                  <Nav.Link 
                    eventKey="profile" 
                    active={activeTab === 'profile'}
                    onClick={() => handleTabChange('profile')}
                  >
                    Profile
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link 
                    eventKey="questions" 
                    active={activeTab === 'questions'}
                    onClick={() => handleTabChange('questions')}
                  >
                    Questions
                  </Nav.Link>
                </Nav.Item>
              </Nav>
            </Card.Header>

            <Card.Body>
              {activeTab === 'profile' && (
                <div>
                  <h5>Profile Information</h5>
                  <Row className="mb-3">
                    <Col md={3} className="fw-bold">Username:</Col>
                    <Col md={9}>{user.username}</Col>
                  </Row>
                  <Row className="mb-3">
                    <Col md={3} className="fw-bold">Email:</Col>
                    <Col md={9}>{user.email}</Col>
                  </Row>
                  {user.phoneNumber && (
                    <Row className="mb-3">
                      <Col md={3} className="fw-bold">Phone:</Col>
                      <Col md={9}>{user.phoneNumber}</Col>
                    </Row>
                  )}
                  <Row className="mb-3">
                    <Col md={3} className="fw-bold">Reputation:</Col>
                    <Col md={9}>{user.reputation}</Col>
                  </Row>

                  {isOwnProfile && (
                    <div className="mt-4">
                      <Button variant="outline-primary">Edit Profile</Button>
                    </div>
                  )}

                  {isModerator && !isOwnProfile && (
                    <div className="mt-4">
                      <Button variant="outline-danger" className="me-2">
                        {user.banned ? 'Unban User' : 'Ban User'}
                      </Button>
                      <Button variant="outline-primary">
                        {user.isModerator ? 'Remove Moderator' : 'Make Moderator'}
                      </Button>
                    </div>
                  )}
                </div>
              )}

              {activeTab === 'questions' && (
                <div>
                  <h5>{questions.length} Questions</h5>
                  {questions.length === 0 ? (
                    <p className="text-center py-3">This user hasn't asked any questions yet.</p>
                  ) : (
                    <div>
                      {questions.map(question => (
                        <QuestionCard key={question.id} question={question} />
                      ))}
                    </div>
                  )}
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default UserProfilePage;
