import React from 'react';
import { Link } from 'react-router-dom';
import { Badge } from 'react-bootstrap';

const QuestionCard = ({ question }) => {
  // Format date relative to current time
  const formatDate = (dateString) => {
    if (!dateString) return "unknown time";

    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      // Check if hours or minutes
      const diffHours = Math.floor(diffTime / (1000 * 60 * 60));
      if (diffHours === 0) {
        const diffMinutes = Math.floor(diffTime / (1000 * 60));
        return `${diffMinutes} ${diffMinutes === 1 ? 'minute' : 'minutes'} ago`;
      }
      return `${diffHours} ${diffHours === 1 ? 'hour' : 'hours'} ago`;
    } else if (diffDays === 1) {
      return 'yesterday';
    } else if (diffDays < 7) {
      return `${diffDays} days ago`;
    } else if (diffDays < 30) {
      const weeks = Math.floor(diffDays / 7);
      return `${weeks} ${weeks === 1 ? 'week' : 'weeks'} ago`;
    } else if (diffDays < 365) {
      const months = Math.floor(diffDays / 30);
      return `${months} ${months === 1 ? 'month' : 'months'} ago`;
    } else {
      const years = Math.floor(diffDays / 365);
      return `${years} ${years === 1 ? 'year' : 'years'} ago`;
    }
  };

  // Extract tags from comma-separated string or use array if available
  const getTags = () => {
    if (!question.tags) return [];
    if (Array.isArray(question.tags)) return question.tags;
    return question.tags.split(',').map(tag => tag.trim()).filter(tag => tag);
  };

  // Get status indicator
  const getStatusIndicator = () => {
    if (!question.status) return null;

    let variant = "secondary";
    let icon = null;

    switch(question.status.toLowerCase()) {
      case "solved":
        variant = "success";
        icon = "check-circle-fill";
        break;
      case "open":
        variant = "primary";
        icon = "question-circle-fill";
        break;
      case "closed":
        variant = "danger";
        icon = "x-circle-fill";
        break;
      default:
        variant = "secondary";
        icon = "circle-fill";
    }

    return (
      <Badge bg={variant} className="me-2 px-2 py-1 d-inline-flex align-items-center">
        {icon && <i className={`bi bi-${icon} me-1`}></i>}
        {question.status}
      </Badge>
    );
  };

  return (
    <div className="question-card border-bottom p-3">
      <div className="d-flex">
        {/* Vote count and answers count */}
        <div className="me-3 d-flex flex-column align-items-center text-center" style={{minWidth: "70px"}}>
          <div className={`vote-count-badge ${(question.votes || 0) < 0 ? 'text-danger' : ''}`}>
            <div className="fw-bold">{question.votes || 0}</div>
            <div className="small text-muted">votes</div>
          </div>

          <div className={`mt-2 answer-count-badge ${question.status === 'solved' ? 'text-success border border-success rounded px-2' : ''}`}>
            <div className="fw-bold">
              {question.answersCount || 0}
              {question.status === 'solved' && <i className="bi bi-check ms-1 text-success"></i>}
            </div>
            <div className="small text-muted">answers</div>
          </div>
        </div>

        {/* Question content */}
        <div className="flex-grow-1">
          <h3 className="h5 mb-2">
            <Link to={`/questions/${question.id}`} className="text-decoration-none question-title">
              {question.title}
            </Link>
          </h3>

          <div className="question-preview text-muted mb-2">
            {question.text && (
              question.text.length > 150 
                ? `${question.text.substring(0, 150)}...` 
                : question.text
            )}
          </div>

          <div className="d-flex justify-content-between align-items-center flex-wrap">
            <div className="tags mb-2">
              {getStatusIndicator()}

              {getTags().map((tag, index) => (
                <Link to={`/tags/${tag}`} key={index} className="tag-badge text-decoration-none">
                  {tag}
                </Link>
              ))}
            </div>

            <div className="question-meta d-flex align-items-center small text-muted">
              <Link to={`/users/${question.authorId}`} className="text-decoration-none me-2">
                <div className="d-inline-flex align-items-center text-secondary">
                  <div className="user-avatar-sm me-1">
                    {question.authorUsername ? question.authorUsername.charAt(0).toUpperCase() : 'U'}
                  </div>
                  <span>{question.authorUsername}</span>
                </div>
              </Link>

              <span className="me-2">â€¢</span>
              <span title={new Date(question.createdAt).toLocaleString()}>
                {formatDate(question.createdAt)}
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuestionCard;
