import React, { useContext } from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { AuthContext } from '../services/AuthContext';
import { voteService } from '../services/api';

const AnswerItem = ({ answer, isAccepted, onAccept, questionAuthorId }) => {
  const { currentUser, isAuthenticated } = useContext(AuthContext);

  const handleVote = async (voteType) => {
    if (!isAuthenticated) return;

    try {
      await voteService.voteAnswer(currentUser.id, answer.id, voteType);
      window.location.reload();
    } catch (error) {
      console.error("Error voting on answer:", error);
    }
  };

  const canAccept = isAuthenticated && currentUser?.id === questionAuthorId;

  return (
    <Card className={`mb-3 ${isAccepted ? 'accepted-answer' : ''}`}>
      <Card.Body>
        <div className="d-flex">
          <div className="vote-buttons">
            <Button 
              variant="outline-secondary" 
              size="sm"
              onClick={() => handleVote('UP')}
              disabled={!isAuthenticated}
            >
              <i className="bi bi-caret-up-fill"></i>
            </Button>
            <div className="vote-count">{answer.votes}</div>
            <Button 
              variant="outline-secondary" 
              size="sm"
              onClick={() => handleVote('DOWN')}
              disabled={!isAuthenticated}
            >
              <i className="bi bi-caret-down-fill"></i>
            </Button>
            {isAccepted && (
              <div className="mt-2 text-success">
                <i className="bi bi-check-circle-fill"></i>
              </div>
            )}
          </div>

          <div className="flex-grow-1">
            <Card.Text>{answer.text}</Card.Text>

            {answer.code && (
              <pre className="bg-light p-3 rounded">
                <code>{answer.code}</code>
              </pre>
            )}

            <div className="d-flex justify-content-between align-items-center mt-3">
              <div>
                {canAccept && !isAccepted && (
                  <Button 
                    variant="outline-success" 
                    size="sm"
                    onClick={() => onAccept(answer.id)}
                  >
                    Accept this answer
                  </Button>
                )}
              </div>

              <div className="user-info">
                <span className="text-muted me-2">
                  answered {new Date(answer.createdAt).toLocaleDateString()}
                </span>
                <Link to={`/users/${answer.authorId}`} className="text-decoration-none">
                  {answer.authorName || 'User'}
                </Link>
              </div>
            </div>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export default AnswerItem;
