import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Badge, Button, Form, Alert } from 'react-bootstrap';
import { questionService, answerService, voteService } from '../services/api';
import { AuthContext } from '../services/AuthContext';
import AnswerItem from '../components/AnswerItem';

const QuestionDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentUser, isAuthenticated } = useContext(AuthContext);

  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [answerText, setAnswerText] = useState('');
  const [answerCode, setAnswerCode] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [answerError, setAnswerError] = useState('');

  const loadQuestionData = async () => {
    try {
      console.log("Fetching question with ID:", id);
      const response = await questionService.getQuestionById(id);
      console.log("Question response:", response);

      if (response && response.data) {
        setQuestion(response.data);
        setError(null);
      } else {
        console.error("Question response is missing data");
        setError("Failed to load question details: Missing data");
      }
    } catch (err) {
      console.error('Error details:', err.response || err);
      setError('Failed to load question details. Please try again later.');
      console.error('Error fetching question:', err);
    }
  };

  const loadAnswersData = async () => {
    try {
      console.log("Fetching answers for question ID:", id);
      const response = await answerService.getAnswersByQuestion(id);
      console.log("Answers response:", response);

      if (response && response.data) {
        setAnswers(response.data);
      } else {
        console.error("Answers response is missing data");
        // Not setting an error here so at least the question displays
      }
    } catch (err) {
      console.error('Error details:', err.response || err);
      console.error('Error fetching answers:', err);
      // Not setting an error here so at least the question displays
    }
  };

  useEffect(() => {
    console.log("QuestionDetailPage mounted with ID:", id);

    const loadData = async () => {
      setLoading(true);
      await loadQuestionData();
      await loadAnswersData();
      setLoading(false);
    };

    loadData();
  }, [id]);

  const handleVote = async (voteType) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    try {
      console.log("Voting on question:", { userId: currentUser.id, questionId: id, voteType });
      const response = await voteService.voteQuestion(currentUser.id, id, voteType);
      console.log("Vote response:", response);
      loadQuestionData(); // Reload question to update vote count
    } catch (error) {
      console.error("Error details:", error.response || error);
      console.error("Error voting on question:", error);
    }
  };

  const handleSubmitAnswer = async (e) => {
    e.preventDefault();

    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    if (!answerText.trim()) {
      setAnswerError('Answer text is required');
      return;
    }

    setSubmitting(true);
    setAnswerError('');

    try {
      console.log("Submitting answer:", { 
        authorId: currentUser.id, 
        questionId: id, 
        text: answerText, 
        image: answerCode || "" 
      });

      // Using the correct parameter structure that matches AnswerController.createAnswer
      const response = await answerService.createAnswer(
        currentUser.id, 
        id, 
        answerText, 
        answerCode || ""
      );

      console.log("Answer submission response:", response);
      setAnswerText('');
      setAnswerCode('');
      await loadAnswersData(); // Reload answers to show the new one
    } catch (err) {
      console.error("Error details:", err.response || err);
      setAnswerError('Failed to submit your answer. Please try again.');
      console.error('Error submitting answer:', err);
    } finally {
      setSubmitting(false);
    }
  };

  const handleAcceptAnswer = async (answerId) => {
    try {
      console.log("Accepting answer:", { questionId: id, answerId });
      const response = await questionService.acceptAnswer(id, answerId);
      console.log("Accept answer response:", response);
      await loadQuestionData();
      await loadAnswersData();
    } catch (error) {
      console.error("Error details:", error.response || error);
      console.error("Error accepting answer:", error);
    }
  };

  if (loading) {
    return <Container className="py-5 text-center">Loading...</Container>;
  }

  if (error || !question) {
    return (
      <Container className="py-5">
        <Alert variant="danger">{error || 'Question not found'}</Alert>
        <div className="text-center mt-3">
          <Button as={Link} to="/" variant="primary">Return to Questions</Button>
        </div>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <Row>
        <Col>
          <Link to="/" className="text-decoration-none mb-3 d-inline-block">
            &larr; Back to All Questions
          </Link>

          <Card className="mb-4">
            <Card.Body>
              <div className="d-flex">
                <div className="vote-buttons">
                  <Button 
                    variant="outline-secondary" 
                    size="sm"
                    onClick={() => handleVote('UP')}
                    disabled={!isAuthenticated}
                  >
                    <i className="bi bi-caret-up-fill"></i>
                  </Button>
                  <div className="vote-count">{question.votes || 0}</div>
                  <Button 
                    variant="outline-secondary" 
                    size="sm"
                    onClick={() => handleVote('DOWN')}
                    disabled={!isAuthenticated}
                  >
                    <i className="bi bi-caret-down-fill"></i>
                  </Button>
                </div>

                <div className="flex-grow-1">
                  <Card.Title className="mb-3">{question.title || "Untitled Question"}</Card.Title>
                  <Card.Text>{question.text || "No description provided"}</Card.Text>

                  {question.image && (
                    <div className="mb-3">
                      <img 
                        src={question.image} 
                        alt="Question attachment" 
                        className="img-fluid"
                        style={{ maxHeight: '300px' }}
                      />
                    </div>
                  )}

                  <div className="mt-3">
                    {question.tags && typeof question.tags === 'string' ? (
                      question.tags.split(',').map((tag, index) => (
                        <Badge
                          as={Link}
                          to={`/tags/${tag.trim()}`}
                          bg="secondary"
                          className="tag-badge text-decoration-none"
                          key={index}
                        >
                          {tag.trim()}
                        </Badge>
                      ))
                    ) : question.tagList && Array.isArray(question.tagList) ? (
                      question.tagList.map((tag, index) => (
                        <Badge
                          as={Link}
                          to={`/tags/${typeof tag === 'string' ? tag : tag.name}`}
                          bg="secondary"
                          className="tag-badge text-decoration-none"
                          key={index}
                        >
                          {typeof tag === 'string' ? tag : tag.name}
                        </Badge>
                      ))
                    ) : null}
                  </div>

                  <div className="d-flex justify-content-between align-items-center mt-4">
                    <div>
                      {isAuthenticated && currentUser.id === question.authorId && (
                        <Button 
                          as={Link} 
                          to={`/questions/edit/${question.id}`}
                          variant="outline-secondary"
                          size="sm"
                          className="me-2"
                        >
                          Edit Question
                        </Button>
                      )}
                    </div>

                    <div className="user-info">
                      <span className="text-muted me-2">
                        asked {question.createdAt ? new Date(question.createdAt).toLocaleDateString() : "unknown date"}
                      </span>
                      <Link to={`/users/${question.authorId}`} className="text-decoration-none">
                        {question.authorName || question.authorUsername || 'User'}
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            </Card.Body>
          </Card>

          <h4 className="mb-3">
            {answers.length} {answers.length === 1 ? 'Answer' : 'Answers'}
          </h4>

          {answers.map(answer => (
            <AnswerItem 
              key={answer.id} 
              answer={answer} 
              isAccepted={question.acceptedAnswerId === answer.id}
              onAccept={handleAcceptAnswer}
              questionAuthorId={question.authorId}
            />
          ))}

          <div className="mt-5">
            <h4>Your Answer</h4>

            {!isAuthenticated ? (
              <Card className="mb-4">
                <Card.Body className="text-center">
                  <p>You must be logged in to answer questions.</p>
                  <Button as={Link} to="/login" variant="primary">Log in</Button>
                  <span className="mx-2">or</span>
                  <Button as={Link} to="/register" variant="outline-primary">Sign up</Button>
                </Card.Body>
              </Card>
            ) : (
              <Form onSubmit={handleSubmitAnswer}>
                {answerError && <Alert variant="danger">{answerError}</Alert>}

                <Form.Group className="mb-3" controlId="answerText">
                  <Form.Label>Answer Text</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={6}
                    value={answerText}
                    onChange={(e) => setAnswerText(e.target.value)}
                    placeholder="Write your answer here..."
                    disabled={submitting}
                  />
                </Form.Group>

                <Form.Group className="mb-3" controlId="answerCode">
                  <Form.Label>Code (Optional)</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={4}
                    value={answerCode}
                    onChange={(e) => setAnswerCode(e.target.value)}
                    placeholder="Add code if relevant to your answer..."
                    disabled={submitting}
                    className="font-monospace"
                  />
                </Form.Group>

                <Button variant="primary" type="submit" disabled={submitting}>
                  {submitting ? 'Submitting...' : 'Post Your Answer'}
                </Button>
              </Form>
            )}
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default QuestionDetailPage;
