import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Card, Nav, Button, Alert, Badge } from 'react-bootstrap';
import { userService, questionService, answerService } from '../services/api';
import { AuthContext } from '../services/AuthContext';
import QuestionCard from '../components/QuestionCard';
import UserStats from '../components/UserStats';
import UserActivity from '../components/UserActivity';
import UserAnswers from '../components/UserAnswers';
import EditProfileForm from '../components/EditProfileForm';

const UserProfilePage = () => {
  const { id } = useParams();
  const { currentUser, isModerator } = useContext(AuthContext);

  const [user, setUser] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [activities, setActivities] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('profile');
  const [isEditing, setIsEditing] = useState(false);

  // Function to load user data and related information
  const loadUserData = async () => {
    setLoading(true);
    try {
      console.log("Fetching user data for ID:", id);
      const userResponse = await userService.getUserById(id);
      console.log("User data response:", userResponse);
      setUser(userResponse.data);

      // Load additional data based on the active tab
      if (activeTab === 'questions' || activeTab === 'stats') {
        console.log("Fetching questions for user ID:", id);
        const questionsResponse = await questionService.getQuestionsByAuthor(id);
        console.log("User questions response:", questionsResponse);
        setQuestions(questionsResponse.data || []);
      }

      if (activeTab === 'answers' || activeTab === 'stats') {
        console.log("Fetching answers for user ID:", id);
        const answersResponse = await answerService.getAnswersByAuthor(id);
        console.log("User answers response:", answersResponse);
        setAnswers(answersResponse.data || []);
      }

      if (activeTab === 'activity') {
        console.log("Fetching activity for user ID:", id);
        const activityResponse = await userService.getUserActivity(id);
        console.log("User activity response:", activityResponse);
        setActivities(activityResponse.data || []);
      }

      if (activeTab === 'stats') {
        console.log("Fetching statistics for user ID:", id);
        const statsResponse = await userService.getUserStatistics(id);
        console.log("User statistics response:", statsResponse);
        setStats(statsResponse.data || null);
      }

      setError(null);
    } catch (err) {
      console.error('Error details:', err.response || err);
      setError('Failed to load user profile. Please try again later.');
      console.error('Error fetching user data:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadUserData();
  }, [id, activeTab]);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    // Reset editing state when changing tabs
    if (tab !== 'profile') {
      setIsEditing(false);
    }
  };

  const handleEditProfile = () => {
    setIsEditing(true);
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
  };

  const handleProfileUpdate = (updatedUser) => {
    setUser(updatedUser);
    setIsEditing(false);
    // Refresh user data to ensure we have the latest
    loadUserData();
  };

  const handleToggleBanUser = async () => {
    if (!user || !isModerator) return;

    try {
      await userService.updateUser(user.id, {
        ...user,
        banned: !user.banned
      });

      // Refresh user data
      loadUserData();
    } catch (err) {
      console.error('Error toggling user ban status:', err);
      setError('Failed to update user status');
    }
  };

  const handleToggleModeratorStatus = async () => {
    if (!user || !isModerator) return;

    try {
      await userService.updateUser(user.id, {
        ...user,
        isModerator: !user.isModerator
      });

      // Refresh user data
      loadUserData();
    } catch (err) {
      console.error('Error toggling moderator status:', err);
      setError('Failed to update user status');
    }
  };

  const isOwnProfile = currentUser && currentUser.id === parseInt(id);

  if (loading && !user) {
    return (
      <Container className="py-5">
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-3 text-muted">Loading profile information...</p>
        </div>
      </Container>
    );
  }

  if (error || !user) {
    return (
      <Container className="py-5">
        <Alert variant="danger">{error || 'User not found'}</Alert>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <div className="profile-header mb-4">
        <Row>
          <Col md={3} className="text-center text-md-start">
            <div className="user-avatar-xl mb-3 d-inline-block">
              {user.username.charAt(0).toUpperCase()}
            </div>
            <h2 className="mb-1">{user.username}</h2>
            <p className="text-muted">
              Member since {new Date(user.createdAt).toLocaleDateString('en-US', { year: 'numeric', month: 'long' })}
            </p>

            {user.isModerator && (
              <Badge bg="primary" className="me-2 mb-2 px-3 py-2">
                <i className="bi bi-shield-check me-1"></i> Moderator
              </Badge>
            )}

            {user.banned && (
              <Badge bg="danger" className="mb-2 px-3 py-2">
                <i className="bi bi-slash-circle me-1"></i> Banned
              </Badge>
            )}
          </Col>

          <Col md={5} className="mt-3 mt-md-0">
            {user.bio && (
              <div className="mb-3">
                <p>{user.bio}</p>
              </div>
            )}

            <div className="d-flex flex-wrap">
              {user.location && (
                <div className="me-4 mb-2">
                  <div className="text-muted mb-1 small">
                    <i className="bi bi-geo-alt me-1"></i> Location
                  </div>
                  <div>{user.location}</div>
                </div>
              )}

              {user.website && (
                <div className="me-4 mb-2">
                  <div className="text-muted mb-1 small">
                    <i className="bi bi-globe me-1"></i> Website
                  </div>
                  <div>
                    <a href={user.website} target="_blank" rel="noopener noreferrer" className="text-decoration-none">
                      {user.website.replace(/^https?:\/\//, '')}
                    </a>
                  </div>
                </div>
              )}
            </div>
          </Col>

          <Col md={4} className="mt-3 mt-md-0">
            <Row>
              <Col xs={4} className="text-center">
                <div className="stat-card">
                  <div className="stat-value">{questions.length}</div>
                  <div className="stat-label">Questions</div>
                </div>
              </Col>
              <Col xs={4} className="text-center">
                <div className="stat-card">
                  <div className="stat-value">{answers.length}</div>
                  <div className="stat-label">Answers</div>
                </div>
              </Col>
              <Col xs={4} className="text-center">
                <div className="stat-card">
                  <div className="stat-value">{user.reputation || 0}</div>
                  <div className="stat-label">Reputation</div>
                </div>
              </Col>
            </Row>

            <div className="mt-3 social-links">
              {user.github && (
                <a 
                  href={`https://github.com/${user.github}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="github"
                >
                  <i className="bi bi-github"></i>
                </a>
              )}

              {user.linkedin && (
                <a 
                  href={`https://linkedin.com/in/${user.linkedin}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="linkedin"
                >
                  <i className="bi bi-linkedin"></i>
                </a>
              )}

              {user.twitter && (
                <a 
                  href={`https://twitter.com/${user.twitter}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="twitter"
                >
                  <i className="bi bi-twitter"></i>
                </a>
              )}
            </div>
          </Col>
        </Row>

        {isOwnProfile && !isEditing && (
          <div className="mt-3">
            <Button 
              variant="outline-primary" 
              className="rounded-pill px-4"
              onClick={handleEditProfile}
            >
              <i className="bi bi-pencil me-2"></i>
              Edit Profile
            </Button>
          </div>
        )}
      </div>

      {isModerator && !isOwnProfile && (
        <Card className="mb-4 shadow-sm border-0">
          <Card.Header className="bg-light">
            <h5 className="mb-0">
              <i className="bi bi-shield me-2"></i>
              Moderator Actions
            </h5>
          </Card.Header>
          <Card.Body>
            <div className="d-flex flex-wrap gap-2">
              <Button 
                variant={user.banned ? "outline-success" : "outline-danger"} 
                className="rounded-pill px-4"
                onClick={handleToggleBanUser}
              >
                {user.banned ? (
                  <>
                    <i className="bi bi-unlock me-2"></i>
                    Unban User
                  </>
                ) : (
                  <>
                    <i className="bi bi-ban me-2"></i>
                    Ban User
                  </>
                )}
              </Button>

              <Button 
                variant="outline-primary" 
                className="rounded-pill px-4"
                onClick={handleToggleModeratorStatus}
              >
                {user.isModerator ? (
                  <>
                    <i className="bi bi-shield-minus me-2"></i>
                    Remove Moderator
                  </>
                ) : (
                  <>
                    <i className="bi bi-shield-plus me-2"></i>
                    Make Moderator
                  </>
                )}
              </Button>
            </div>
          </Card.Body>
        </Card>
      )}

      {!isEditing ? (
        <Card className="mb-4 shadow-sm border-0">
          <Card.Header className="bg-white">
            <Nav variant="tabs" className="nav-tabs-lightly">
              <Nav.Item>
                <Nav.Link 
                  active={activeTab === 'profile'}
                  onClick={() => handleTabChange('profile')}
                  className="cursor-pointer"
                >
                  <i className="bi bi-person me-2"></i>
                  Profile
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link 
                  active={activeTab === 'stats'}
                  onClick={() => handleTabChange('stats')}
                  className="cursor-pointer"
                >
                  <i className="bi bi-bar-chart me-2"></i>
                  Stats
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link 
                  active={activeTab === 'activity'}
                  onClick={() => handleTabChange('activity')}
                  className="cursor-pointer"
                >
                  <i className="bi bi-clock-history me-2"></i>
                  Activity
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link 
                  active={activeTab === 'questions'}
                  onClick={() => handleTabChange('questions')}
                  className="cursor-pointer"
                >
                  <i className="bi bi-question-circle me-2"></i>
                  Questions
                </Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link 
                  active={activeTab === 'answers'}
                  onClick={() => handleTabChange('answers')}
                  className="cursor-pointer"
                >
                  <i className="bi bi-chat-left-text me-2"></i>
                  Answers
                </Nav.Link>
              </Nav.Item>
            </Nav>
          </Card.Header>

          <Card.Body>
            {activeTab === 'profile' && (
              <div>
                <h5 className="mb-4">Contact Information</h5>

                <Row className="mb-3">
                  <Col md={3} className="fw-bold text-muted">Email:</Col>
                  <Col md={9}>{user.email}</Col>
                </Row>
                {user.phoneNumber && (
                  <Row className="mb-3">
                    <Col md={3} className="fw-bold text-muted">Phone:</Col>
                    <Col md={9}>{user.phoneNumber}</Col>
                  </Row>
                )}
                <Row className="mb-3">
                  <Col md={3} className="fw-bold text-muted">Member Since:</Col>
                  <Col md={9}>
                    {new Date(user.createdAt).toLocaleDateString('en-US', { 
                      year: 'numeric', 
                      month: 'long', 
                      day: 'numeric' 
                    })}
                  </Col>
                </Row>
              </div>
            )}

            {activeTab === 'stats' && (
              <UserStats stats={stats} loading={loading} />
            )}

            {activeTab === 'activity' && (
              <UserActivity activities={activities} loading={loading} />
            )}

            {activeTab === 'questions' && (
              <div>
                <h5 className="mb-4">
                  <i className="bi bi-question-circle me-2"></i>
                  {questions.length} Questions
                </h5>
                {questions.length === 0 ? (
                  <div className="text-center py-4 text-muted">
                    <i className="bi bi-search display-4 d-block mb-3"></i>
                    <p>This user hasn't asked any questions yet.</p>
                  </div>
                ) : (
                  <div>
                    {questions.map(question => (
                      <QuestionCard key={question.id} question={question} />
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === 'answers' && (
              <UserAnswers answers={answers} loading={loading} />
            )}
          </Card.Body>
        </Card>
      ) : (
        <EditProfileForm 
          user={user} 
          onUpdate={handleProfileUpdate} 
          onCancel={handleCancelEdit} 
        />
      )}
    </Container>
  );
};

export default UserProfilePage;
