import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link, useNavigate, useLocation } from 'react-router-dom';
import { Container, Row, Col, Card, Badge, Button, Form, Alert } from 'react-bootstrap';
import { questionService, answerService, voteService } from '../services/api';
import { AuthContext } from '../services/AuthContext';
import AnswerItem from '../components/AnswerItem';

const QuestionDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { currentUser, isAuthenticated } = useContext(AuthContext);

  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [answerText, setAnswerText] = useState('');
  const [answerCode, setAnswerCode] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [answerError, setAnswerError] = useState('');

  const loadQuestionData = async () => {
    try {
      if (!id) {
        setError("Question ID is missing. Please check the URL.");
        setLoading(false);
        return;
      }

      // Validate id to prevent issues with 'undefined' or other invalid values
      if (id === 'undefined' || id === 'null') {
        setError("Invalid question ID format. Please check the URL.");
        setLoading(false);
        return;
      }

      const response = await questionService.getQuestionById(id);

      if (response && response.data) {
        // Make sure both id and questionId exist on the question object
        const questionData = response.data;
        if (questionData.questionId && !questionData.id) {
          questionData.id = questionData.questionId;
        } else if (questionData.id && !questionData.questionId) {
          questionData.questionId = questionData.id;
        }

        setQuestion(questionData);
        setError(null);
      } else {
        setError("Failed to load question details: Missing data");
      }
    } catch (err) {
      console.error("Error loading question:", err);
      setError(err.message || 'Failed to load question details. Please try again later.');
      setLoading(false);
    }
  };

  const loadAnswersData = async (questionData) => {
    try {
      // Use the passed question data if available, otherwise fall back to the state
      let targetQuestion = questionData || question;

      // First try to use the question ID from the loaded question
      let questionId = targetQuestion ? (targetQuestion.questionId || targetQuestion.id) : null;

      // Fall back to URL param if necessary
      if (!questionId && id) {
        questionId = id;
      }

      if (!questionId) {
        return;
      }

      // Validate questionId before making API call
      if (questionId === 'undefined' || questionId === 'null') {
        console.error("Invalid question ID for loading answers");
        setError("Invalid question ID. Please return to the questions page.");
        return;
      }

      const response = await answerService.getAnswersByQuestion(questionId);

      if (response && response.data) {
        setAnswers(response.data);
      }
    } catch (err) {
      console.error("Error loading answers:", err);
      // Not setting an error here so at least the question displays
    }
  };

  useEffect(() => {
    if (!id) {
      setError("Question ID is missing. Please check the URL.");
      setLoading(false);
      return;
    }

    const loadData = async () => {
      setLoading(true);

      try {
        // Load question data first
        if (!id) {
          setError("Question ID is missing. Please check the URL.");
          return;
        }

        if (id === 'undefined' || id === 'null') {
          setError("Invalid question ID format. Please check the URL.");
          return;
        }

        const response = await questionService.getQuestionById(id);

        if (response && response.data) {
          // Make sure both id and questionId exist on the question object
          const questionData = response.data;
          if (questionData.questionId && !questionData.id) {
            questionData.id = questionData.questionId;
          } else if (questionData.id && !questionData.questionId) {
            questionData.questionId = questionData.id;
          }

          setQuestion(questionData);
          setError(null);

          // Load answers immediately with the question data
          await loadAnswersData(questionData);
        } else {
          setError("Failed to load question details: Missing data");
        }
      } catch (err) {
        console.error("Error loading question:", err);
        setError(err.message || 'Failed to load question details. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [id]);

  // Add a fallback effect to load answers if question changes
  useEffect(() => {
    if (question && !loading) {
      loadAnswersData(question);
    }
  }, [question?.id, question?.questionId]);

  const handleVote = async (voteType) => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    if (!question) {
      return;
    }

    // Get the question ID from the loaded question data
    const questionId = question.questionId || question.id;

    if (!questionId) {
      return;
    }

    // Get user ID, handling both property names
    const userId = currentUser?.id || currentUser?.userId;

    if (!userId) {
      return;
    }

    try {
      await voteService.voteQuestion(userId, questionId, voteType);
      loadQuestionData(); // Reload question to update vote count
    } catch (error) {
      // Silently handle error
    }
  };

  const handleSubmitAnswer = async (e) => {
    e.preventDefault();

    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    if (!answerText.trim()) {
      setAnswerError('Answer text is required');
      return;
    }

    if (!question) {
      setAnswerError('Cannot submit answer: Question data is missing');
      return;
    }

    // Get the question ID from the loaded question data, not from URL params
    const questionId = question.questionId || question.id;

    if (!questionId) {
      setAnswerError('Cannot submit answer due to missing question ID');
      return;
    }

    // Get user ID, handling both property names
    const userId = currentUser?.id || currentUser?.userId;

    if (!currentUser || !userId) {
      setAnswerError('Cannot submit answer: User information is missing');
      return;
    }

    setSubmitting(true);
    setAnswerError('');

    try {
      // Submit the answer
      await answerService.createAnswer(
        userId,
        questionId,
        answerText.trim(), 
        answerCode || ""
      );

      setAnswerText('');
      setAnswerCode('');
      // Load answers using the current question data
      await loadAnswersData(question);
    } catch (err) {
      let errorMessage = 'Failed to submit your answer. Please try again.';

      // Extract specific error message if available
      if (err.response && err.response.data) {
        if (typeof err.response.data === 'string') {
          errorMessage = `Server error: ${err.response.data}`;
        } else if (err.response.data.message) {
          errorMessage = `Server error: ${err.response.data.message}`;
        } else if (err.response.data.error) {
          errorMessage = `Server error: ${err.response.data.error}`;
        }
      }

      setAnswerError(errorMessage);
    } finally {
      setSubmitting(false);
    }
  };

  const handleAcceptAnswer = async (answerId) => {
    if (!question) {
      console.error("Cannot accept answer: Question data is missing");
      return;
    }

    // Get the question ID from the loaded question data
    const questionId = question.questionId || question.id;

    if (!questionId) {
      console.error("Cannot accept answer: Question ID is missing from question data");
      return;
    }

    // Validate IDs before making API call
    if (questionId === 'undefined' || answerId === 'undefined') {
      console.error("Invalid IDs for accepting answer");
      return;
    }

    try {
      await questionService.acceptAnswer(questionId, answerId);

      // Reload question data first, then reload answers with the updated question
      const response = await questionService.getQuestionById(id);
      if (response && response.data) {
        const updatedQuestion = response.data;
        // Ensure both id and questionId exist
        if (updatedQuestion.questionId && !updatedQuestion.id) {
          updatedQuestion.id = updatedQuestion.questionId;
        } else if (updatedQuestion.id && !updatedQuestion.questionId) {
          updatedQuestion.questionId = updatedQuestion.id;
        }

        setQuestion(updatedQuestion);
        await loadAnswersData(updatedQuestion);
      }
    } catch (error) {
      console.error("Error accepting answer:", error);
      setError(`Failed to accept answer: ${error.message || 'Unknown error'}`);
      setTimeout(() => setError(null), 5000); // Clear error after 5 seconds
    }
  };

  if (loading) {
    return (
      <Container className="py-5">
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-3 text-muted">Loading question details...</p>
        </div>
      </Container>
    );
  }

  if (error || !question) {
    return (
      <Container className="py-5">
        <Alert variant="danger">{error || 'Question not found'}</Alert>
        <div className="text-center mt-3">
          <Button 
            onClick={() => navigate('/', { replace: true })} 
            variant="primary"
          >
            Return to Questions
          </Button>
        </div>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <div className="mb-3">
        <Link to="/" className="text-decoration-none d-inline-flex align-items-center">
          <i className="bi bi-arrow-left me-2"></i>
          Back to All Questions
        </Link>
      </div>

      <div className="question-detail">
        <div className="d-flex">
          <div className="vote-buttons">
            <Button 
              variant="outline-secondary" 
              size="sm"
              onClick={() => handleVote('UP')}
              disabled={!isAuthenticated}
              className="rounded-circle"
            >
              <i className="bi bi-caret-up-fill"></i>
            </Button>
            <div className="vote-count">{question.votes || 0}</div>
            <Button 
              variant="outline-secondary" 
              size="sm"
              onClick={() => handleVote('DOWN')}
              disabled={!isAuthenticated}
              className="rounded-circle"
            >
              <i className="bi bi-caret-down-fill"></i>
            </Button>
          </div>

          <div className="flex-grow-1">
            <h1 className="question-title">{question.title}</h1>

            <div className="question-meta">
              <div>
                <i className="bi bi-calendar3 me-1"></i>
                Asked {new Date(question.createdAt).toLocaleDateString('en-US', {
                  year: 'numeric',
                  month: 'short',
                  day: 'numeric'
                })}
              </div>

              <div>
                <i className="bi bi-person me-1"></i>
                <Link to={`/users/${question.authorId}`} className="text-decoration-none">
                  {question.authorUsername}
                </Link>
              </div>

              {question.status && (
                <div>
                  <Badge bg={question.status === 'solved' ? 'success' : 
                           question.status === 'received' ? 'primary' : 'secondary'}>
                    {question.status}
                  </Badge>
                </div>
              )}
            </div>

            <div className="question-content mb-4">
              <div dangerouslySetInnerHTML={{ __html: question.text.replace(/\n/g, '<br>') }}></div>

              {question.image && (
                <div className="code-block mt-3 p-3 bg-light rounded">
                  <pre>{question.image}</pre>
                </div>
              )}
            </div>

            <div className="d-flex flex-wrap mb-3">
              {question.tags && question.tags.split(',').map((tag, index) => (
                <Link
                  to={`/tags/${tag.trim()}`}
                  className="tag-badge text-decoration-none"
                  key={index}
                >
                  {tag.trim()}
                </Link>
              ))}
            </div>
          </div>
        </div>
      </div>

      <h2 className="my-4">{answers.length} {answers.length === 1 ? 'Answer' : 'Answers'}</h2>

      {answers.length === 0 ? (
        <div className="text-center py-4 text-muted bg-white rounded-lg shadow-sm p-5">
          <i className="bi bi-chat-square-text display-1 mb-3 d-block opacity-50"></i>
          <p className="lead">No answers yet. Be the first to answer this question!</p>
          {isAuthenticated && (
            <Button 
              variant="primary" 
              size="lg"
              className="rounded-pill px-4"
              onClick={() => document.getElementById('answer-form').scrollIntoView({ behavior: 'smooth' })}
            >
              <i className="bi bi-pencil me-2"></i>
              Write an Answer
            </Button>
          )}
        </div>
      ) : (
        <div className="answers-container">
          {answers.map((answer) => (
            <AnswerItem
              key={answer.id}
              answer={answer}
              questionOwnerId={question.authorId}
              currentUserId={currentUser?.id}
              isAccepted={question.acceptedAnswerId === answer.id}
              onAcceptAnswer={handleAcceptAnswer}
            />
          ))}
        </div>
      )}

      {isAuthenticated ? (
        <Card className="mt-5 shadow-sm border-0" id="answer-form">
          <Card.Header className="bg-white">
            <h3 className="mb-0">Your Answer</h3>
          </Card.Header>
          <Card.Body>
            {answerError && <Alert variant="danger">{answerError}</Alert>}

            <Form onSubmit={handleSubmitAnswer}>
              <Form.Group className="mb-3">
                <Form.Label>Answer Text</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={6}
                  value={answerText}
                  onChange={(e) => setAnswerText(e.target.value)}
                  placeholder="Write your answer here..."
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Code (optional)</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={4}
                  value={answerCode}
                  onChange={(e) => setAnswerCode(e.target.value)}
                  placeholder="Add code examples here..."
                />
              </Form.Group>

              <div className="d-flex justify-content-end">
                <Button 
                  type="submit" 
                  variant="primary" 
                  disabled={submitting}
                  className="px-4 rounded-pill"
                >
                  {submitting ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                      Submitting...
                    </>
                  ) : (
                    <>
                      <i className="bi bi-send me-2"></i>
                      Post Your Answer
                    </>
                  )}
                </Button>
              </div>
            </Form>
          </Card.Body>
        </Card>
      ) : (
        <Card className="mt-5 shadow-sm text-center p-4 border-0 bg-light">
          <Card.Body>
            <h4 className="mb-3">Want to answer this question?</h4>
            <p>You need to log in first.</p>
            <Button as={Link} to="/login" variant="primary" className="rounded-pill px-4 me-2">
              Log in
            </Button>
            <Button as={Link} to="/register" variant="outline-primary" className="rounded-pill px-4">
              Sign up
            </Button>
          </Card.Body>
        </Card>
      )}
    </Container>
  );
};

export default QuestionDetailPage;
