import axios from 'axios';

// Use explicit base URL for all requests
const API_URL = 'http://localhost:8080';

// Add request interceptor for debugging
axios.interceptors.request.use(request => {
  console.log('API Request:', {
    url: request.url,
    method: request.method,
    data: request.data,
    params: request.params
  });
  return request;
}, error => {
  console.error('Request error:', error);
  return Promise.reject(error);
});

// Add response interceptor for debugging
axios.interceptors.response.use(response => {
  console.log('API Response:', {
    url: response.config.url,
    status: response.status,
    data: response.data
  });
  return response;
}, error => {
  console.error('Response error:', error.config ? {
    url: error.config.url,
    status: error.response?.status,
    data: error.response?.data
  } : error);
  return Promise.reject(error);
});

// Create axios instance with CORS config
const apiClient = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  },
  withCredentials: true
});

// User related API calls
export const userService = {
  login: (username, password) => apiClient.post(`/users/login`, { username, password }),
  register: (username, email, password, phoneNumber) => apiClient.post(`/users/create`, { username, email, password, phoneNumber }),
  getUserById: (id) => apiClient.get(`/users/id/${id}`),
  getUserByUsername: (username) => apiClient.get(`/users/username/${username}`),
  updateUser: (id, data) => apiClient.put(`/users/${id}`, data),
  getAllUsers: () => apiClient.get(`/users/all`)
};

// Question related API calls
export const questionService = {
  getAllQuestions: () => apiClient.get(`/questions/all`),
  getQuestionById: (id) => apiClient.get(`/questions/find/${id}`),
  getQuestionsByAuthor: (authorId) => apiClient.get(`/questions/author/${authorId}`),
  getQuestionsByTag: (tagName) => apiClient.get(`/questions/tag/${tagName}`),
  getQuestionsByStatus: (status) => apiClient.get(`/questions/status/${status}`),
  searchQuestions: (keyword) => apiClient.get(`/questions/search?keyword=${keyword}`),
  createQuestion: (authorId, title, text, image, tags) => 
    apiClient.post(`/questions/create`, { authorId, title, text, image, tags }),
  updateQuestion: (id, data, userId) => apiClient.put(`/questions/update/${id}?userId=${userId}`, data),
  acceptAnswer: (questionId, answerId) => apiClient.put(`/questions/${questionId}/accept/${answerId}`),
  deleteQuestion: (id, userId) => apiClient.delete(`/questions/delete/${id}?userId=${userId}`)
};

// Answer related API calls
export const answerService = {
  getAnswersByQuestion: (questionId) => apiClient.get(`/answers/question/${questionId}`),
  createAnswer: (authorId, questionId, text, code) => 
    apiClient.post(`/answers/create`, { authorId, questionId, text, code }),
  updateAnswer: (id, data, userId) => apiClient.put(`/answers/update/${id}?userId=${userId}`, data),
  deleteAnswer: (id, userId) => apiClient.delete(`/answers/delete/${id}?userId=${userId}`)
};

// Vote related API calls
export const voteService = {
  voteQuestion: (userId, questionId, voteType) => 
    apiClient.post(`/votes/question`, { userId, questionId, voteType }),
  voteAnswer: (userId, answerId, voteType) => 
    apiClient.post(`/votes/answer`, { userId, answerId, voteType })
};

// Tag related API calls
export const tagService = {
  getAllTags: () => apiClient.get(`/tags/all`),
  getTagByName: (name) => apiClient.get(`/tags/${name}`)
};
