import React from 'react';
import { Link } from 'react-router-dom';
import { Card, Badge } from 'react-bootstrap';

const QuestionCard = ({ question }) => {
  return (
    <Card className="question-card">
      <Card.Body>
        <div className="d-flex">
          <div className="text-center me-3">
            <div>
              <strong>{question.votes}</strong>
              <div className="text-muted small">votes</div>
            </div>
            <div className="mt-2">
              <strong>{question.answers}</strong>
              <div className="text-muted small">answers</div>
            </div>
          </div>

          <div className="flex-grow-1">
            <Card.Title>
              <Link to={`/questions/${question.id}`} className="text-decoration-none">
                {question.title}
              </Link>
            </Card.Title>

            <Card.Text>
              {question.text.length > 150 
                ? `${question.text.substring(0, 150)}...` 
                : question.text}
            </Card.Text>

            <div className="d-flex justify-content-between">
              <div>
                {question.tags && question.tags.split(',').map((tag, index) => (
                  <Badge
                    as={Link}
                    to={`/tags/${tag.trim()}`}
                    bg="secondary"
                    className="tag-badge text-decoration-none"
                    key={index}
                  >
                    {tag.trim()}
                  </Badge>
                ))}
              </div>

              <div className="user-info">
                <span className="text-muted me-2">
                  asked {new Date(question.createdAt).toLocaleDateString()}
                </span>
                <Link to={`/users/${question.authorId}`} className="text-decoration-none">
                  {question.authorName || 'User'}
                </Link>
              </div>
            </div>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export default QuestionCard;
